    private void sManagementActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sManagementActionPerformed
        String[] options = {"Add Student", "Edit Student", "View Student"};
        
        int choice = JOptionPane.showOptionDialog(
            this,
            "What action would you like to perform?",
            "Student Management",
            JOptionPane.DEFAULT_OPTION,
            JOptionPane.INFORMATION_MESSAGE,
            null,
            options,
            options[0]
        );
        
        switch (choice){
            case 0:
                addStudent();
                break;
            case 1:
                editStudent();
                break;
            case 2:    
                viewStudent();
                break;
            default:
                JOptionPane.showMessageDialog(this, "No action selected.");
                break;
        }
    }//GEN-LAST:event_sManagementActionPerformed

