    private void logoutBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_logoutBtnActionPerformed
        int confirm = JOptionPane.showConfirmDialog(
            this,
            "Are you sure you want to log out?",
            "Confirm Logout",
            JOptionPane.YES_NO_OPTION,
            JOptionPane.QUESTION_MESSAGE
        );

        if (confirm == JOptionPane.YES_OPTION) {
            String currentRole = "Registrar"; 

            Logo logout = new Logo(currentRole);
            logout.setVisible(true);

            this.dispose(); 
        };
    }//GEN-LAST:event_logoutBtnActionPerformed
    private void addStudent(){
        rStudent aStudent = new rStudent(email);
        aStudent.setVisible(true);
        this.dispose();
    }
    private void editStudent(){
        rEditstudent edit = new rEditstudent(email);
        edit.setVisible(true);
        this.dispose();
    }
    private void viewStudent(){
        SDatabase studentdb = new SDatabase(email);
        studentdb.setVisible(true);
        this.dispose();
    }
